    public static boolean increasingTriplet5(int[] nums) {
        if (nums.length < 2) return false;
        //take el[2], assume that it is a min num, verify if it's true, if not move index
        //if bigger number on the beginning el[0] and el[1], skip them
        int minIndex =0;
        for(int i=0; i<=2; i++){
            if(nums[i]<nums[minIndex]) {
                minIndex = i;
            }
        }
        //verify if last 2 elements in the array are smaller or equal min, if its true skip them
        int len =nums.length;
        if(nums[nums.length-1]<=nums[minIndex] && nums[nums.length-2]>nums[minIndex]) len-=1;
        if(nums[nums.length-1]<=nums[minIndex] && nums[nums.length-2]<=nums[minIndex]) len-=2;

        //look in for max
        int j = minIndex;
        while (j < len) {
            int max = nums[j];
            int temp = nums[j];
            int counter = 0;
            for (int i = j + 1; i < len; i++) {
                if (nums[i] > max && nums[i] > temp) {
                    counter++;
                    temp = max;
                    max = nums[i];
                }
                if (nums[i] > temp && nums[i] < max) {
                    max = nums[i];
                }
                if (counter == 2) {
                    return true;
                }
            }
            j++;
        }
        return false;
    }

