    public static boolean increasingTriplet1(int[] nums) {
        if (nums.length < 2) return false;

        int j = 0;
        while (j < nums.length) {
            int max = nums[j];
            int temp = nums[j];
            int counter = 0;
            for (int i = j + 1; i < nums.length; i++) {
                if (nums[i] > max && nums[i] > temp) {
                    counter++;
                    temp = max;
                    max = nums[i];
                }
                if (nums[i] > temp && nums[i] < max) {
                    max = nums[i];
                }
                if (counter == 2) {
                    return true;
                }
            }
            j++;
        }

        return false;//increasingTriplet(Arrays.copyOfRange(nums,1,nums.length));
    }

    //--------------------------------------------------------------
    public static boolean increasingTriplet2(int[] nums) {
        if (nums.length < 2) return false;

        int i = 1;
        int counter = 0;
        while (i < nums.length) {
            if (nums[i - 1] < nums[i]) {
                counter++;
                if (counter == 2) return true;
            } else {
                counter = 0;
            }
            i++;
        }
        return false;
    }
